INSERT INTO Dispatch ( StreamId, CommitSequence) SELECT @StreamId, @CommitSequence /*FROM DUAL*/ WHERE NOT EXISTS ( SELECT * FROM Dispatch WHERE StreamId = @StreamId AND CommitSequence = @CommitSequence );
INSERT INTO Commits ( StreamId, CommitId, CommitSequence, StreamRevision, CommitStamp, Headers, Payload ) SELECT @StreamId, @CommitId, @CommitSequence, @StreamRevision, @CommitStamp, @Headers, @Payload /*FROM DUAL*/ WHERE NOT EXISTS ( SELECT * FROM Commits WHERE StreamId = @StreamId AND CommitId <> @CommitId AND CommitSequence >= @CommitSequence );
INSERT INTO Streams ( StreamId, HeadRevision ) SELECT @StreamId, @StreamRevision /*FROM DUAL*/ WHERE @CommitSequence = 1 AND NOT EXISTS ( SELECT * FROM Streams WHERE StreamId = @StreamId );
UPDATE Streams SET HeadRevision = @StreamRevision WHERE StreamId = @StreamId AND @CommitSequence > 1 AND HeadRevision < @StreamRevision;
