/*LimitedQueryFormat*/
SELECT OuterQuery.* FROM (
  SELECT InnerQuery.*, ROWNUM AS ROW_NUMBER_VAL FROM (
    {0}    
  ) InnerQuery
) OuterQuery
WHERE ROW_NUMBER_VAL > :Skip AND ROW_NUMBER_VAL <= (:Limit + :Skip)

/*PagedQueryFormat*/
SELECT *
FROM ( {0},
       ROW_NUMBER() OVER({1}) AS ROW_NUMBER_VAL
       {2}
) PagedQueryFormat
WHERE ROW_NUMBER_VAL > :Skip AND ROW_NUMBER_VAL <= (:Limit + :Skip)
